import React, { useState } from 'react';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { Cell as CellType } from '../types';
import Cell from './Cell';

interface SortableCellProps {
  cell: CellType;
  notebookId: string;
}

const SortableCell: React.FC<SortableCellProps> = ({ cell, notebookId }) => {
  const [isEditing, setIsEditing] = useState(false);

  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: cell.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  // Custom event handlers that work with both drag-and-drop and button clicks
  const handleMouseDown = (e: React.MouseEvent) => {
    // If clicking on a button, don't start drag
    if (e.target instanceof HTMLButtonElement) {
      return;
    }
    // Otherwise, let the drag listeners handle it
    if (listeners.onMouseDown) {
      listeners.onMouseDown(e);
    }
  };

  const handleTouchStart = (e: React.TouchEvent) => {
    // If touching a button, don't start drag
    if (e.target instanceof HTMLButtonElement) {
      return;
    }
    // Otherwise, let the drag listeners handle it
    if (listeners.onTouchStart) {
      listeners.onTouchStart(e);
    }
  };

  return (
    <div
      ref={setNodeRef}
      style={style}
      {...attributes}
      className={isEditing ? "" : "cursor-move"}
      onMouseDown={handleMouseDown}
      onTouchStart={handleTouchStart}
      onClick={(e) => {
        console.log('SortableCell clicked, target:', e.target);
        // Prevent drag-and-drop from interfering with button clicks
        if (e.target instanceof HTMLButtonElement) {
          console.log('Button clicked in SortableCell, stopping propagation');
          e.stopPropagation();
          e.preventDefault();
        }
      }}
      onKeyDown={(e) => {
        // Prevent drag-and-drop from interfering with keyboard events when editing
        if (isEditing) {
          e.stopPropagation();
        }
      }}
    >
      <Cell 
        cell={cell} 
        notebookId={notebookId} 
        onEditingChange={setIsEditing}
      />
    </div>
  );
};

export default SortableCell;
